function dateTimeDownWeb(a,c,b){this.countdown=a;this.translation_obj=JSON.parse(b);this.do_cd=function(d){if(this.countdown<=0){document.getElementById(c).innerHTML=this.translation_obj.stop}else{if(d<0||d>6){d=6}var f=this.convert_to_time(this.countdown,d);document.getElementById(c).innerHTML=f;var e=this;setTimeout(function(){e.do_cd(d)},1000)}this.countdown=this.countdown-1};this.convert_to_time=function(g,e){years=Math.floor(g/31536000);diff_s=g-years*31536000;month=Math.floor(diff_s/2628000);diff_s=diff_s-month*2628000;days=Math.floor(diff_s/86400);diff_s=diff_s-days*86400;hours=Math.floor(diff_s/3600);diff_s=diff_s-hours*3600;minutes=Math.floor(diff_s/60);diff_s=diff_s-minutes*60;seconds=diff_s;if(seconds<10){seconds_show="0"+seconds}else{seconds_show=seconds}if(minutes<10){minutes_show="0"+minutes}else{minutes_show=minutes}if(hours<10){hours_show="0"+hours}else{hours_show=hours}var d="";var f=0;if(years!=0){d+=years+" "+this.translation_obj.years+" ";f++}if(e==f){return d}if(f!=0){f++;if(month!=0){d+=month+" "+this.translation_obj.months+" "}}else{if(month!=0){d+=month+" "+this.translation_obj.months+" ";f++}}if(e==f){return d}if(f!=0){f++;if(days!=0){d+=days+" "+this.translation_obj.days+" "}}else{if(days!=0){d+=days+" "+this.translation_obj.days+" ";f++}}if(e==f){return d}if(f!=0){f++;if(hours_show!=0){d+=hours_show+" "+this.translation_obj.hours+" "}}else{if(hours!=0){d+=hours_show+" "+this.translation_obj.hours+" ";f++}}if(e==f){return d}if(f!=0){f++;if(minutes_show!=0){d+=minutes_show+" "+this.translation_obj.minutes+" "}}else{if(minutes!=0){d+=minutes_show+" "+this.translation_obj.minutes+" ";f++}}if(e==f){return d}if(seconds_show!=0){d+=seconds_show+" "+this.translation_obj.seconds+" "}return d}};