INSERT INTO `#__ticketmaster_emails` (`emailid`, `template_type`, `mailbody`, `secured`, `userid`, `approved`, `mailsubject`, `lastchange`, `description`, `from_email`, `from_name`, `receive_bcc`, `reply_to_email`, `reply_to_name`, `published`) VALUES
(100, 1, '<p><span class="text"><span style="font-family: tahoma,arial,helvetica,sans-serif;"><span style="font-size: 10pt;">Dear %%NAME%%, <br /><br /></span></span><span style="font-family: tahoma,arial,helvetica,sans-serif;"><span style="font-size: 10pt;">Recently you have ordered some tickets with us, unfortunatly we still have not received your payment. If your payment does not come in within the next 24 hours your order will be cancelled and the tickets may be ordered again by other customers.</span></span></span></p>\r\n<p><span class="text"><span style="font-family: tahoma,arial,helvetica,sans-serif;"><span style="font-size: 10pt;">Please click the next link (or copy and paste) to make an online payment to us.</span></span></span></p>\r\n<p><a href="%%PAYMENTLINK%%"><span class="text"><span style="font-family: tahoma,arial,helvetica,sans-serif;"><span style="font-size: 10pt;"><span style="font-family: tahoma, arial, helvetica, sans-serif;">%%PAYMENTLINK%%</span></span></span></span></a></p>\r\n<p><span class="text"><span style="font-family: tahoma,arial,helvetica,sans-serif;"><span style="font-size: 10pt;">After you have paid your tickets, they will be sent immediatly to this email address.</span></span></span></p>\r\n<p><span class="text"><span style="font-family: tahoma,arial,helvetica,sans-serif;"><span style="font-size: 10pt;">Thank you for your attention!<br /><br />Kind Regards,<br />[YOUR NAME]</span></span></span></p>', 1, 0, 0, 'Payment request for your tickets.', '2013-07-26 09:13:27', 'Will be sent when the component is reminding the customer to pay.', 'robert.dam@outlook.com', 'Ticketmaster Demo Website', 0, 'robert.dam@outlook.com', 'Ticketmaster Demo Website', 1),
(101, 1, '<p><span class="text"><span style="font-family: tahoma,arial,helvetica,sans-serif;"><span style="font-size: 10pt;">Dear %%NAME%%, <br /><br /></span></span><span style="font-family: tahoma,arial,helvetica,sans-serif;"><span style="font-size: 10pt;">Thank you for ordering tickets with us, we just received the question to resend the payment link to finish your order. Below you will find the</span></span></span><span style="font-size: 10pt; font-family: tahoma, arial, helvetica, sans-serif;">&nbsp;link, please click (or copy and paste) to make an online payment to us.</span></p>\r\n<p><a href="%%PAYMENTLINK%%"><span style="font-size: 10pt; font-family: tahoma, arial, helvetica, sans-serif;">%%PAYMENTLINK%%</span></a></p>\r\n<p><span class="text"><span style="font-family: tahoma,arial,helvetica,sans-serif;"><span style="font-size: 10pt;">After you have paid your tickets, they will be sent immediatly to this email address.</span></span></span></p>\r\n<p><span class="text"><span style="font-family: tahoma,arial,helvetica,sans-serif;"><span style="font-size: 10pt;">Thank you for your attention!<br /><br />Kind Regards,<br />[YOUR NAME]</span></span></span></p>', 1, 0, 0, 'Payment requested', '2013-07-26 07:13:27', 'Will be sent if your customer asks for payment data to pay their order.', 'robert.dam@outlook.com', 'Ticketmaster Demo Website', 0, 'robert.dam@outlook.com', 'Ticketmaster Demo Website', 1);
